import React, { useState } from "react";
import axios from "axios";

const Career = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    phone: "",
    dob: "",
    permanentAddress: "",
    qualification: "",
    totalExperience: "",
    currentSalary: "",
    expectedSalary: "",
    appliedRole: "",
    resume: null, // ✅ resume as file object
  });

  // Handle text, number, date, and select inputs
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle file input separately
  const handleFileChange = (e) => {
    setFormData({ ...formData, resume: e.target.files[0] });
  };

  // Submit form
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formDataToSend = new FormData();
      Object.keys(formData).forEach((key) => {
        formDataToSend.append(key, formData[key]);
      });

      const res = await axios.post("http://localhost:5000/api/apply", formDataToSend, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("Application Submitted Successfully!");
      console.log(res.data);

      // Reset form after submit
      setFormData({
        fullName: "",
        email: "",
        phone: "",
        dob: "",
        permanentAddress: "",
        qualification: "",
        totalExperience: "",
        currentSalary: "",
        expectedSalary: "",
        appliedRole: "",
        resume: null,
      });
    } catch (error) {
      console.error(error);
      alert("Error submitting application.");
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Career Application</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div className="row">
          {/* Full Name */}
          <div className="col-md-6 mb-3">
            <input
              type="text"
              name="fullName"
              value={formData.fullName}
              onChange={handleChange}
              className="form-control"
              placeholder="Full Name"
              required
            />
          </div>

          {/* Email */}
          <div className="col-md-6 mb-3">
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="form-control"
              placeholder="Email"
              required
            />
          </div>

          {/* Phone */}
          <div className="col-md-6 mb-3">
            <input
              type="text"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              className="form-control"
              placeholder="Phone Number"
              required
            />
          </div>

          {/* DOB */}
          <div className="col-md-6 mb-3">
            <input
              type="date"
              name="dob"
              value={formData.dob}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          {/* Permanent Address */}
          <div className="col-12 mb-3">
            <input
              type="text"
              name="permanentAddress"
              value={formData.permanentAddress}
              onChange={handleChange}
              className="form-control"
              placeholder="Permanent Address"
              required
            />
          </div>

          {/* Qualification */}
          <div className="col-md-6 mb-3">
            <input
              type="text"
              name="qualification"
              value={formData.qualification}
              onChange={handleChange}
              className="form-control"
              placeholder="Qualification"
              required
            />
          </div>

          {/* Total Experience */}
          <div className="col-md-6 mb-3">
            <input
              type="number"
              name="totalExperience"
              value={formData.totalExperience}
              onChange={handleChange}
              className="form-control"
              placeholder="Total Experience (years)"
              required
            />
          </div>

          {/* Current Salary */}
          <div className="col-md-6 mb-3">
            <input
              type="number"
              name="currentSalary"
              value={formData.currentSalary}
              onChange={handleChange}
              className="form-control"
              placeholder="Current Salary"
              required
            />
          </div>

          {/* Expected Salary */}
          <div className="col-md-6 mb-3">
            <input
              type="number"
              name="expectedSalary"
              value={formData.expectedSalary}
              onChange={handleChange}
              className="form-control"
              placeholder="Expected Salary"
              required
            />
          </div>

          {/* Applied Role */}
          <div className="col-md-6 mb-3">
            <input
              type="text"
              name="appliedRole"
              value={formData.appliedRole}
              onChange={handleChange}
              className="form-control"
              placeholder="Applied Role"
            />
          </div>

          {/* Resume Upload */}
          <div className="col-md-6 mb-3">
            <input
              type="file"
              name="resume"
              accept=".pdf,.doc,.docx"
              className="form-control"
              required
              onChange={handleFileChange} // ✅ FIXED
            />
            {formData.resume && (
              <small className="text-muted">
                Selected file: {formData.resume.name}
              </small>
            )}
          </div>
        </div>

        <div className="text-center">
          <button type="submit" className="btn btn-primary">
            Submit Application
          </button>
        </div>
      </form>
    </div>
  );
};

export default Career;
