import React, { useState } from "react";
import axios from "axios";

const Career = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    dob: "",
    address: "",
    qualification: "",
    experience: "",
    currentSalary: "",
    expectedSalary: "",
    resumeFile: null, // ✅ updated key
  });

  const [selectedJob, setSelectedJob] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, resumeFile: e.target.files[0] }); // ✅ updated key
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append("fullName", formData.name);
    data.append("email", formData.email);
    data.append("phone", formData.phone);
    data.append("dob", formData.dob);
    data.append("address", formData.address); // ✅ backend expects "address"
    data.append("qualification", formData.qualification);
    data.append("jobRole", selectedJob?.title || ""); // ✅ backend expects "jobRole"
    data.append("totalExperience", formData.experience);
    data.append("currentSalary", formData.currentSalary);
    data.append("expectedSalary", formData.expectedSalary);

    // ✅ backend multer expects "resumeFile"
    data.append("resumeFile", formData.resumeFile);

    try {
      const res = await axios.post("http://localhost:5000/api/apply", data, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("✅ Application submitted successfully!");
      console.log("Saved Application:", res.data);
      setSelectedJob(null);
    } catch (err) {
      console.error("❌ Error submitting application:", err.response?.data || err.message);
      alert("❌ Error submitting application");
    }
  };

  return (
    <div className="career-container">
      <h2>Career Application</h2>

      <form onSubmit={handleSubmit} className="career-form">
        <input
          type="text"
          name="name"
          placeholder="Full Name"
          value={formData.name}
          onChange={handleChange}
          required
        />

        <input
          type="email"
          name="email"
          placeholder="Email Address"
          value={formData.email}
          onChange={handleChange}
          required
        />

        <input
          type="tel"
          name="phone"
          placeholder="Phone Number"
          value={formData.phone}
          onChange={handleChange}
          required
        />

        <input
          type="date"
          name="dob"
          value={formData.dob}
          onChange={handleChange}
          required
        />

        <input
          type="text"
          name="address"
          placeholder="Permanent Address"
          value={formData.address}
          onChange={handleChange}
          required
        />

        <input
          type="text"
          name="qualification"
          placeholder="Qualification"
          value={formData.qualification}
          onChange={handleChange}
          required
        />

        <input
          type="text"
          name="experience"
          placeholder="Total Experience"
          value={formData.experience}
          onChange={handleChange}
        />

        <input
          type="text"
          name="currentSalary"
          placeholder="Current Salary"
          value={formData.currentSalary}
          onChange={handleChange}
        />

        <input
          type="text"
          name="expectedSalary"
          placeholder="Expected Salary"
          value={formData.expectedSalary}
          onChange={handleChange}
        />

        {/* ✅ matches backend multer.single("resumeFile") */}
        <input
          type="file"
          name="resumeFile"
          accept=".pdf,.doc,.docx"
          required
          onChange={handleFileChange}
        />

        <button type="submit">Submit Application</button>
      </form>
    </div>
  );
};

export default Career;
